import React from 'react'
import { Modal, Descriptions, Divider, Image, Tabs, Card, Typography, Table, Tag, Row, Col } from 'antd'
import type { Product, ProductTranslation } from 'src/types/product.type'
import type { Language } from 'src/types/language.type'
import { formatCurrency } from 'src/utils/utils'
import { useQuery } from '@tanstack/react-query'
import languageApi from 'src/apis/language.api'
import { StarOutlined } from '@ant-design/icons'
import ProductReviewSection from 'src/pages/ManageProduct/ProductReviewSection'

const { Text } = Typography
const { TabPane } = Tabs

interface ProductDetailProps {
  visible: boolean
  product: Product
  onClose: () => void
  onEdit?: (product: Product) => void
  onManageTranslations?: (product: Product) => void
}

export default function ProductDetail({ visible, product, onClose, onEdit, onManageTranslations }: ProductDetailProps) {
  // Get languages for translation tabs
  const { data: languagesData } = useQuery({
    queryKey: ['languages'],
    queryFn: () => languageApi.getList(),
    enabled: visible
  })

  // Group translations by language for easier access
  const translationsByLanguage = React.useMemo(() => {
    if (!product.productTranslations) return {}

    return product.productTranslations.reduce((acc, translation) => {
      acc[translation.languageId] = translation
      return acc
    }, {} as Record<string, ProductTranslation>)
  }, [product.productTranslations])

  // SKU columns for the table
  const skuColumns = [
    {
      title: 'SKU',
      dataIndex: 'value',
      key: 'value'
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      render: (price: number) => `$${formatCurrency(price)}`
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock'
    },
    {
      title: 'Image',
      dataIndex: 'image',
      key: 'image',
      render: (image: string) => (
        <Image
          src={image || '/placeholder-image.png'}
          alt='SKU Image'
          width={40}
          height={40}
          style={{ objectFit: 'cover', borderRadius: '4px' }}
          fallback='data:image/png;base64,'
        />
      )
    },
    {
      title: 'Created At',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => new Date(date).toLocaleDateString()
    }
  ]

  return (
    <Modal
      title={
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <span>Product Details</span>
        </div>
      }
      open={visible}
      onCancel={onClose}
      footer={null}
      width={1200} // Increased width to accommodate reviews
    >
      <div style={{ display: 'flex', alignItems: 'flex-start', marginBottom: 20 }}>
        <div style={{ marginRight: 20 }}>
          <Image
            src={product.images[0] || '/placeholder-image.png'}
            alt={product.name}
            width={120}
            height={120}
            style={{ objectFit: 'cover', borderRadius: '8px' }}
            fallback='data:image/png;base64,'
          />
        </div>
        <div>
          <h2 style={{ margin: 0 }}>{product.name}</h2>
          <div style={{ display: 'flex', gap: 16, marginTop: 8 }}>
            <Text type='secondary'>Base Price: ${formatCurrency(product.basePrice)}</Text>
            {product.virtualPrice > 0 && (
              <Text type='secondary' delete>
                Virtual Price: ${formatCurrency(product.virtualPrice)}
              </Text>
            )}
          </div>
          <div style={{ marginTop: 8 }}>
            <Text type='secondary'>Brand: {product.brand?.name || 'N/A'}</Text>
          </div>
          <div style={{ marginTop: 8 }}>
            <Text type='secondary'>
              Categories:{' '}
              {product.categories?.map((category) => (
                <Tag key={category.id}>{category.name}</Tag>
              ))}
            </Text>
          </div>
        </div>
      </div>

      <Tabs defaultActiveKey='1'>
        <TabPane tab='Product Information' key='1'>
          <Divider orientation='left'>Product Images</Divider>
          <div style={{ marginBottom: 20 }}>
            <Row gutter={[16, 16]}>
              {product.images.map((image, index) => (
                <Col key={index} span={6}>
                  <Image
                    src={image || '/placeholder-image.png'}
                    alt={`Product Image ${index + 1}`}
                    style={{ width: '100%', height: 150, objectFit: 'cover', borderRadius: '8px' }}
                    fallback='data:image/png;base64,'
                  />
                </Col>
              ))}
            </Row>
          </div>

          <Divider orientation='left'>Basic Information</Divider>
          <Descriptions bordered column={2}>
            <Descriptions.Item label='ID'>{product.id}</Descriptions.Item>
            <Descriptions.Item label='Published At'>
              {new Date(product.publishedAt).toLocaleDateString()}
            </Descriptions.Item>
            <Descriptions.Item label='Base Price'>${formatCurrency(product.basePrice)}</Descriptions.Item>
            <Descriptions.Item label='Virtual Price'>${formatCurrency(product.virtualPrice)}</Descriptions.Item>
            <Descriptions.Item label='Brand'>{product.brand?.name || 'N/A'}</Descriptions.Item>
            <Descriptions.Item label='Categories'>
              {product.categories?.map((category) => (
                <Tag key={category.id}>{category.name}</Tag>
              ))}
            </Descriptions.Item>
            <Descriptions.Item label='Created At'>{new Date(product.createdAt).toLocaleDateString()}</Descriptions.Item>
            <Descriptions.Item label='Updated At'>{new Date(product.updatedAt).toLocaleDateString()}</Descriptions.Item>
            <Descriptions.Item label='Created By'>{product.createdById}</Descriptions.Item>
            <Descriptions.Item label='Updated By'>{product.updatedById || '-'}</Descriptions.Item>
          </Descriptions>

          {product.deletedAt && (
            <>
              <Divider />
              <Descriptions bordered column={2}>
                <Descriptions.Item label='Deleted At'>
                  {new Date(product.deletedAt).toLocaleDateString()}
                </Descriptions.Item>
                <Descriptions.Item label='Deleted By'>{product.deletedById || '-'}</Descriptions.Item>
              </Descriptions>
            </>
          )}

          <Divider orientation='left'>Variants</Divider>
          <div style={{ marginBottom: 20 }}>
            {product.variants && product.variants.length > 0 ? (
              <Row gutter={[16, 16]}>
                {product.variants.map((variant, index) => (
                  <Col key={index} span={12}>
                    <Card title={variant.value} size='small'>
                      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
                        {variant.options.map((option, optIndex) => (
                          <Tag key={optIndex}>{option}</Tag>
                        ))}
                      </div>
                    </Card>
                  </Col>
                ))}
              </Row>
            ) : (
              <Text type='secondary'>No variants defined for this product.</Text>
            )}
          </div>

          <Divider orientation='left'>SKUs</Divider>
          <div style={{ marginBottom: 20 }}>
            {product.skus && product.skus.length > 0 ? (
              <Table dataSource={product.skus} columns={skuColumns} rowKey='id' pagination={false} />
            ) : (
              <Text type='secondary'>No SKUs defined for this product.</Text>
            )}
          </div>
        </TabPane>

        <TabPane tab='Translations' key='2'>
          {product.productTranslations && product.productTranslations.length > 0 ? (
            <Tabs defaultActiveKey={product.productTranslations[0]?.languageId}>
              {languagesData?.data.data.map((language: Language) => {
                const translation = translationsByLanguage[language.id]
                return (
                  <TabPane tab={language.name} key={language.id}>
                    {translation ? (
                      <Card>
                        <Descriptions bordered column={1}>
                          <Descriptions.Item label='Name'>{translation.name}</Descriptions.Item>
                          <Descriptions.Item label='Description'>
                            <div dangerouslySetInnerHTML={{ __html: translation.description }} />
                          </Descriptions.Item>
                          <Descriptions.Item label='Created At'>
                            {new Date(translation.createdAt).toLocaleDateString()}
                          </Descriptions.Item>
                          <Descriptions.Item label='Updated At'>
                            {new Date(translation.updatedAt).toLocaleDateString()}
                          </Descriptions.Item>
                        </Descriptions>
                      </Card>
                    ) : (
                      <Text type='secondary'>No translation available for this language.</Text>
                    )}
                  </TabPane>
                )
              })}
            </Tabs>
          ) : (
            <Text type='secondary'>No translations available for this product.</Text>
          )}
        </TabPane>

        <TabPane
          tab={
            <span className='flex items-center gap-2'>
              <StarOutlined />
              Reviews
            </span>
          }
          key='3'
        >
          <ProductReviewSection productId={product.id} productName={product.name} />
        </TabPane>
      </Tabs>
    </Modal>
  )
}
